import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:firebase_core/firebase_core.dart';
import 'package:flutter/foundation.dart';
import 'package:portfolio/app/routes/app_routes.dart';
import 'app/constants/custom_app_bar.dart';
import 'app/theme/app_theme.dart';
import 'firebase_options.dart';

void main() async {
  WidgetsFlutterBinding.ensureInitialized();

  await Firebase.initializeApp(
    options: DefaultFirebaseOptions.currentPlatform,
  );

  runApp(const ProviderScope(child: MyApp()));
}

class MyApp extends ConsumerWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final isDarkMode = ref.watch(isDarkThemeProvider);
    final colors = getCurrentColors(isDarkMode);

    return MaterialApp(
      title: 'Portfolio',
      debugShowCheckedModeBanner: false,

      navigatorKey: AppRoutes.navigatorKey,
      onGenerateRoute: AppRoutes.onGenerateRoute,
      onUnknownRoute: (settings) => MaterialPageRoute(
        builder: (_) => Scaffold(
          body: Center(child: Text("‚ùå Unknown Route: ${settings.name}")),
        ),
      ),

      // üëá Always use PortfolioLayout for home
      home: const PortfolioLayout(initialPage: "home"),

      theme: ThemeData(
        brightness: Brightness.light,
        primaryColor: colors['primaryColor'],
        scaffoldBackgroundColor: colors['background'],
      ),
      darkTheme: ThemeData(
        brightness: Brightness.dark,
        primaryColor: colors['primaryColor'],
        scaffoldBackgroundColor: colors['background'],
      ),
      themeMode: isDarkMode ? ThemeMode.dark : ThemeMode.light,
    );
  }
}
